        {% endblock %}
    </head>
    <body >
        <a href=\"{{ path('app_accueil') }}\">Accueil</a>
        <a href=\"{{ path('app_sondage_index') }}\">Sondages</a>

        {% if app.user %}
            <a href=\"{{ path('app_moncompte') }}\">Mon compte</a>
            <a href=\"{{ path('app_logout') }}\">Déconnexion</a>
